import React from "react";
import { useState, useEffect } from "react";
import "./Cards.css";
import CardItem from "./CardItem";
import Games from "../Games.json";
import Slider from "react-slick";



function Cards() {
  var settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1
  };

  return (
    <div className="cards">
      <h1>Classic Games!</h1>
      <div className="cards__container">
        <div className="cards__wrapper">
          <Slider className="cards__items" {...settings}>
            {Games.map(game =>(

              <CardItem
                name={game.Name}
                src={game.Image}
                text={game.Description}
                label={game.Genre}
                path="/"
              />
            ))}
          </Slider>
        </div>
      </div>
    </div>
  );
}

export default Cards;
